mapper.update:
UPDATE USER_TBL
SET USER_NICKNAME = #{userNickname}, USER_EMAIL = #{userEmail}, USER_GENDER = #{userGender}, USER_BIRTH = #{userBirth}, USER_TEL = #{userTel}, USER_PHOTO_NAME = #{userPhotoName}, USER_PHOTO_RENAME = #{userPhotoRename}, USER_PHOTO_PATH = #{userPhotoPath}, USER_INFO = #{userInfo}
WHERE USER_ID = #{userId}

UPDATE USER_TBL
SET USER_NAME = #{userName}, USER_NICKNAME = #{userNickname}, USER_GENDER = #{userGender}, USER_BIRTH = #{userBirth}, USER_TEL = #{userTel}, USER_PHOTO_NAME = #{userPhotoName}, USER_PHOTO_RENAME = #{userPhotoRename}, USER_PHOTO_PATH = #{userPhotoPath}, USER_INFO = #{userInfo}
WHERE USER_ID = #{userId}

UPDATE USER_TBL
SET USER_PW = #{userPw}
WHERE USER_ID = #{userId}

UPDATE USER_TBL
SET USER_STATUS = 'N'
WHERE USER_ID = #{userId}

mapper.select:
SELECT *
FROM USER_TBL
WHERE USER_EMAIL = #{userEmail} AND PLATFORM_TYPE = #{platformType}

SELECT COUNT(*) 
FROM USER_TBL
WHERE USER_ID = #{userId}

SELECT COUNT(*) 
FROM USER_TBL
WHERE USER_NICKNAME = #{userNickname}

SELECT COUNT(*) 
FROM USER_TBL
WHERE USER_EMAIL = #{userEmail}

SELECT USER_ID, USER_NAME, USER_EMAIL
FROM USER_TBL
WHERE USER_NAME = #{userName} AND USER_EMAIL = #{userEmail}

SELECT USER_ID, USER_NAME, USER_EMAIL
FROM USER_TBL
WHERE USER_ID = #{userId} AND USER_EMAIL = #{userEmail}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY COMT_DATE DESC) AS RNUM, C.COMT_NO, C.COMT_CONTENT, C.COMT_WRITER, C.COMT_DATE, C.REF_POST_NO, C.P_COMT_NO, C.COMT_STATUS, C.C_BOARD_TYPE, CASE WHEN C.C_BOARD_TYPE = 'J' THEN J.JIQOO_TITLE WHEN C.C_BOARD_TYPE = 'M' THEN M.MOQOO_TITLE ELSE NULL END AS POST_TITLE, U.USER_PHOTO_PATH FROM COMT_TBL C LEFT JOIN JIQOO_TBL J ON C.C_BOARD_TYPE = 'J' AND C.REF_POST_NO = J.JIQOO_NO LEFT JOIN MOQOO_TBL M ON C.C_BOARD_TYPE = 'M' AND C.REF_POST_NO = M.MOQOO_NO LEFT JOIN USER_TBL U ON C.COMT_WRITER = U.USER_ID WHERE C.COMT_WRITER = #{userId} AND C.COMT_STATUS NOT LIKE 'N' AND (J.JIQOO_STATUS NOT LIKE 'N' OR M.MOQOO_STATUS NOT LIKE 'N')
AND TO_CHAR(COMT_DATE, 'yyyy-mm-dd') = #{searchDate}
) WHERE RNUM >= #{startNo} AND RNUM <= #{endNo} ORDER BY COMT_DATE DESC

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY JIQOO_DATE DESC) AS RNUM, JIQOO_NO, JIQOO_W3W, JIQOO_DATE, JIQOO_CTGR, C_IMG_PATH, JIQOO_TITLE, JIQOO_CONTENT, JIQOO_WRITER, USER_PHOTO_PATH, JIQOO_STATUS, J_VIEW_COUNT FROM JIQOO_TBL JOIN CATEGORY_TBL ON JIQOO_CTGR = C_NAME JOIN USER_TBL ON USER_ID = JIQOO_WRITER WHERE JIQOO_WRITER = #{userId} AND JIQOO_STATUS NOT LIKE 'N'
AND TO_CHAR(JIQOO_DATE, 'yyyy-mm-dd') = #{searchDate}
) WHERE RNUM >= #{startNo} AND RNUM <= #{endNo} ORDER BY JIQOO_DATE DESC

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MOQOO_DAY DESC) AS RNUM, MOQOO_NO, MOQOO_W3W, MOQOO_DAY, MOQOO_TAGS, C_IMG_PATH, MOQOO_TITLE, MOQOO_CONTENT, MOQOO_WRITER, USER_PHOTO_PATH, MOQOO_STATUS, M_VIEW_COUNT FROM MOQOO_TBL JOIN CATEGORY_TBL ON MOQOO_TAGS = C_NAME JOIN USER_TBL ON USER_ID = MOQOO_WRITER WHERE MOQOO_WRITER = #{userId} AND MOQOO_STATUS NOT LIKE 'N'
AND TO_CHAR(MOQOO_DAY, 'yyyy-mm-dd') = #{searchDate}
) WHERE RNUM >= #{startNo} AND RNUM <= #{endNo} ORDER BY MOQOO_DAY DESC

SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY POST_DATE DESC) AS RNUM, BOARD_TYPE, POST_NO, L_USER_ID, POST_WRITER, WRITER_PHOTO_PATH, POST_DATE, POST_W3W, POST_TITLE, C.C_IMG_PATH, VIEW_COUNT, POST_STATUS FROM ( SELECT L.REF_BOARD_TYPE AS BOARD_TYPE, L.REF_POST_NO AS POST_NO, L.L_USER_ID AS L_USER_ID, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.JIQOO_WRITER WHEN L.REF_BOARD_TYPE = 'M' THEN M.MOQOO_WRITER END AS POST_WRITER, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN U_J.USER_PHOTO_PATH WHEN L.REF_BOARD_TYPE = 'M' THEN U_M.USER_PHOTO_PATH END AS WRITER_PHOTO_PATH, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.JIQOO_DATE WHEN L.REF_BOARD_TYPE = 'M' THEN M.MOQOO_DAY END AS POST_DATE, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.JIQOO_W3W WHEN L.REF_BOARD_TYPE = 'M' THEN M.MOQOO_W3W END AS POST_W3W, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.JIQOO_TITLE WHEN L.REF_BOARD_TYPE = 'M' THEN M.MOQOO_TITLE END AS POST_TITLE, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.JIQOO_CTGR WHEN L.REF_BOARD_TYPE = 'M' THEN M.MOQOO_TAGS END AS POST_CATEGORY, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.J_VIEW_COUNT WHEN L.REF_BOARD_TYPE = 'M' THEN M.M_VIEW_COUNT END AS VIEW_COUNT, CASE WHEN L.REF_BOARD_TYPE = 'J' THEN J.JIQOO_STATUS WHEN L.REF_BOARD_TYPE = 'M' THEN M.MOQOO_STATUS END AS POST_STATUS FROM LIKE_TBL L LEFT JOIN JIQOO_TBL J ON L.REF_BOARD_TYPE = 'J' AND L.REF_POST_NO = J.JIQOO_NO LEFT JOIN MOQOO_TBL M ON L.REF_BOARD_TYPE = 'M' AND L.REF_POST_NO = M.MOQOO_NO LEFT JOIN USER_TBL U_J ON J.JIQOO_WRITER = U_J.USER_ID LEFT JOIN USER_TBL U_M ON M.MOQOO_WRITER = U_M.USER_ID WHERE L.L_USER_ID = #{userId} ) LEFT JOIN CATEGORY_TBL C ON POST_CATEGORY = C.C_NAME ) WHERE (POST_STATUS NOT LIKE 'N') AND (RNUM >= #{startNo} AND RNUM <= #{endNo})
AND TO_CHAR(POST_DATE, 'yyyy-mm-dd') = #{searchDate}
ORDER BY POST_DATE DESC
